package com.example.mylibrary;

import java.util.ArrayList;

public class Util {
    private static ArrayList<Book> allBooks;
    private static ArrayList<Book> currentlyReadingBooks;
    private static ArrayList<Book> wantToReadBooks;
    private static ArrayList<Book> alreadyReadBooks;

    private static int id = 0;

    public Util() {
        if (allBooks == null) {
            allBooks = new ArrayList<>();
            initAllBooks();
        }

        if (currentlyReadingBooks == null)
            currentlyReadingBooks = new ArrayList<>();

        if (wantToReadBooks == null)
            wantToReadBooks = new ArrayList<>();

        if (alreadyReadBooks == null)
            alreadyReadBooks = new ArrayList<>();


    }


    public static ArrayList<Book> getAllBooks() {
        return allBooks;
    }

    public static ArrayList<Book> getCurrentlyReadingBooks() {
        return currentlyReadingBooks;
    }

    public static ArrayList<Book> getWantToReadBooks() {
        return wantToReadBooks;
    }

    public static ArrayList<Book> getAlreadyReadBooks() {
        return alreadyReadBooks;
    }

    public boolean addCurrentlyReadingBook(Book book) {
        return currentlyReadingBooks.add(book);
    }

    public boolean addWantToReadBook(Book book) {
        return wantToReadBooks.add(book);
    }

    public boolean addAlreadyReadBook(Book book) {
        return alreadyReadBooks.add(book);
    }

    public boolean removeCurrentlyReadingBook(Book book) {
        return currentlyReadingBooks.remove(book);
    }

    public boolean removeWantToReadBook(Book book) {
        return wantToReadBooks.remove(book);
    }

    public boolean removeAlreadyReadBook(Book book) {
        return alreadyReadBooks.remove(book);
    }

    private static void initAllBooks() {
        String name;
        String author;
        int pages = 0;
        String imgUrl = "";
        String description = "";
        ++id;
        name = "The Voyage of the Beagle";
        author = "Charles Darwin";
        pages = 1230;
        imgUrl = "data:image/jpeg;base64,";
        description = "";

        allBooks.add(new Book(id, name, author, pages, imgUrl, description));
        id++;
        allBooks.add(new Book(id, "The Origin of Species", "Charles Darwin",
                1000, "data:image/jpeg;base64,", ""));
        id++;
        allBooks.add(new Book(id, "Philosophiae Naturalis Principia Mathematica", "Isaac Newton",
                1000, "data:image/jpeg;base64,", ""));
        id++;
        allBooks.add(new Book(id, "Dialogue Concerning the Two Chief World Systems", "Galileo Galilei",
                1000, "data:image/jpeg;base64,", ""));


    }
}
